CREATE COLUMN TABLE BPS (
	"MEASURE_ID" BIGINT NOT NULL,
    "MEASURE_DATE" DATE NOT NULL,
    "MEASURE_TIME" TIME NOT NULL,
    "MEASURE_SISTOLIC" DECIMAL NOT NULL,
    "MEASURE_DIASTOLIC" DECIMAL NOT NULL,
    "MEASURE_STATUS" CHAR(4) NOT NULL,
    PRIMARY KEY (MEASURE_ID));


SELECT TOP 1 * FROM BPS ORDER BY MEASURE_ID DESC;
