CREATE COLUMN TABLE EXERCISES (
	"MEASURE_ID" BIGINT NOT NULL,
    "MEASURE_DATE" DATE NOT NULL,
    "MEASURE_TIME" TIME NOT NULL,
    "MEASURE_EXERCISE_TYPE" CHAR(1) NOT NULL,
    "MEASURE_EXERCISE_TYPE_TEXT" CHAR(20) NOT NULL,
    "MEASURE_EXERCISE_TIME" TIME NOT NULL,
    "MEASURE_CALORIES_BURNED" INT NOT NULL,
    PRIMARY KEY (MEASURE_ID));


SELECT TOP 1 * FROM EXERCISES ORDER BY MEASURE_ID DESC;
